package textusebean;

public class student {

	private int id;
	private String fname;
	private String l;

	public student() {
		// TODO Auto-generated constructor stub
	}

	public int getId() {
		return id;
	}

	public void setId(int id) {
		this.id = id;
	}

	public String getFname() {
		return fname;
	}

	public void setFname(String fname) {
		this.fname = fname;
	}

	public String getL() {
		return l;
	}

	public void setL(String l) {
		this.l = l;
	}
}
